const UserContext = createContext()

// the method that is used to pass the context files
export const UserDataProvider = ({children})=>{
    const [user,setUser] = useState([])
const [isLoading,isLoadingSetter] = useState("true");



   useEffect(()=>{
    fetchFeedback()
    console.log("this is from the use state " , user)

},[])


const fetchFeedback = async ()=>{
    const response = await fetch("http://127.0.0.1:8000/movies/")
    const data = await response.json()

    setUser(data)
    isLoadingSetter("false")
   }







const [name,nameSetter] = useState(0);

const BseEffectTrial = (num)=>{
    nameSetter(num)
        }

    return <UserContext.Provider value={{user,name,BseEffectTrial,isLoading}}>
        {children}
    </UserContext.Provider>
}

